import './App.css';
import React from "react";
import axios from 'axios';
import Navbar from './components/Navbar';

function callApi4() {
    const id = document.getElementById('id2').value;
    const hp = document.getElementById('hp2').value;
  
    const url = `http://localhost:3001/api/heroes/${id}`;
  
    axios.put(url, { hp })
      .then(response => {
        console.log(response.data);
        alert('La vida del heroe se ha actualizado');
      })
      .catch(error => {
        console.error(error);
        alert('Error actualizando el dato de vida del heroe');
      });
  }
  
const Update = (props) => {
    return (
      <>
      <div className='derecha' >
      <Navbar brand ="Overwatch Heroes App" />
      <div > 
      <h1 className="home-text08">Actualizar vida</h1>
      <div className="home-container1">
      <input type="text" placeholder="id:" className="input" id='id' />
      <input type="text" placeholder="nombre:" className="input" id='nombre'/>
      <input type="text" placeholder="rol:" className="input" id='rol' />
      <input type="text" placeholder="url de la img:" className="input" id='url'/>
      <input type="text" placeholder="HP:" className="input" id='hp' />
      </div>
      <button className="home-primary button-primary button-lg button" onClick={callApi3}>Registrar</button>
      </div>
      </div>
      </>
    );
  }
  export default Update;
