import './App.css';
import React from "react";
import Navbar from './components/Navbar';
import TableI from './components/table';
import axios from 'axios';

function callApi2() {
  const id = document.querySelector('.input').value;
  axios.get(`http://localhost:3001/api/heroes/:${id}`)
    .then(response => {
      console.log(response);
      const productsContainer = document.getElementById('products-container2');
      productsContainer.innerHTML = JSON.stringify(response.data);
    })
    .catch(error => {
      console.log(error);
    });
}


function callApi3() {
  const id = document.getElementById('id').value;
  const nombre = document.getElementById('nombre').value;
  const rol = document.getElementById('rol').value;
  const url = document.getElementById('url').value;
  const hp = document.getElementById('hp').value;

  const uri = `http://localhost:3001/api/heroes/:${id}`;

  axios.post(uri, {nombre, rol, url, hp})
    .then(response => {
      console.log(response.data);
      alert('El heroe ha sido registrado');
    })
    .catch(error => {
      console.error(error);
      alert('Error registrando al heroe');
    });
}

function callApi4() {
  const id = document.getElementById('id2').value;
  const hp = document.getElementById('hp2').value;

  const url = `http://localhost:3001/api/heroes/${id}`;

  axios.put(url, { hp })
    .then(response => {
      console.log(response.data);
      alert('La vida del heroe se ha actualizado');
    })
    .catch(error => {
      console.error(error);
      alert('Error actualizando el dato de vida del heroe');
    });
}

function callApi5() {
  const id = document.getElementById('id3').value;

  const url = `http://localhost:3001/api/products/${id}`;

  axios.delete(url)
    .then(response => {
      console.log(response.data);
      alert('El Heroe ha sido eliminado de la base de datos');
    })
    .catch(error => {
      console.error(error);
      alert('Error eliminando al heroe');
    });
}

function Home() {
  return (
    <>
    <nav> 
      <h1>Lista de Heores</h1>
    </nav>
    <Navbar brand ="Overwatch Heroes App" />
    <div > 
      <Table />
    </div>
    </>
  );
}

export default Home;
